const data = items[0].json.body

if (!data.fechaCompleta || !data.timezone) {
  throw new Error('Faltan campos: fechaCompleta o timezone')
}

// Interpretar fecha completa como UTC
const userDate = new Date(data.fechaCompleta)
if (isNaN(userDate.getTime())) {
  throw new Error('fechaCompleta no es una fecha v√°lida')
}

// Duraci√≥n
const duracionTexto = data.servicioDetalle?.duracion || '75 minutos'
const minutos = parseInt(duracionTexto.match(/\d+/)?.[0] || '75')
const endDate = new Date(userDate.getTime() + minutos * 60000)

// Visualizaci√≥n local
const options = { timeZone: 'America/Bogota', dateStyle: 'full', timeStyle: 'short' }
const start_local = userDate.toLocaleString('es-CO', options)
const end_local = endDate.toLocaleString('es-CO', options)

const whatsappJid = data.telefono.replace(/\D/g, '') + '@s.whatsapp.net'

return [{
  json: {
    ...data,
    start: userDate.toISOString(),
    end: endDate.toISOString(),
    start_local,
    end_local,
    whatsappJid, // ‚úÖ Agregado aqu√≠
    title: `${data.servicioDetalle?.nombre || 'Sesi√≥n'} - ${data.nombre}`,
    location: data.modalidad === 'presencial'
      ? data.modalidadDetalle?.ubicacion
      : 'Sesi√≥n por Zoom',
    description: [
      `üìç Modalidad: ${data.modalidadDetalle?.tipo || 'N/A'}`,
      data.modalidadDetalle?.indicaciones,
      data.mensaje ? `üìù Mensaje: ${data.mensaje}` : '',
      `‚è± Duraci√≥n: ${data.servicioDetalle?.duracion || '75 minutos'}`
    ].filter(Boolean).join('\n')
  }
}]

const d = $node["Code"].json          // Datos enriquecidos (formato, whatsapp, etc.)
const googleCalendar = $json         // Viene directo del nodo Google Calendar

// ‚úÖ USAR DIRECTAMENTE LOS HORARIOS YA CORREGIDOS DEL FRONTEND
// El frontend ya env√≠a 'd.hora' en formato HH:MM de zona horaria Bogot√°
const startTime = d.hora // Ya viene en formato HH:MM de Bogot√° desde el frontend

// ‚úÖ CALCULAR DURACI√ìN SEG√öN TIPO DE SERVICIO
const duration = d.servicio === 'individual' ? 75 : 120 // Individual: 75 min, Parejas: 120 min

// ‚úÖ CALCULAR ENDTIME BASADO EN DURACI√ìN
const [hours, minutes] = startTime.split(':').map(Number)
const endMinutes = hours * 60 + minutes + duration
const endHours = Math.floor(endMinutes / 60)
const endMins = endMinutes % 60
const endTime = `${endHours.toString().padStart(2, '0')}:${endMins.toString().padStart(2, '0')}`

// ‚úÖ VALIDACIONES DE SEGURIDAD
if (!d.fecha || !d.hora || !d.servicio) {
  throw new Error('Faltan datos esenciales: fecha, hora o servicio')
}

if (!startTime.match(/^\d{2}:\d{2}$/)) {
  throw new Error(`Formato de hora inv√°lido: ${startTime}. Debe ser HH:MM`)
}

// ‚úÖ LOGGING PARA DEBUGGING
console.log('üìÖ Procesando cita:', {
  fecha: d.fecha,
  horaInicio: startTime,
  horaFin: endTime,
  servicio: d.servicio,
  duracion: `${duration} minutos`,
  cliente: d.nombre
})

// ‚úÖ RETORNAR OBJETO ESTRUCTURADO PARA MONGODB
return [{
  json: {
    // üìÜ Datos esenciales para disponibilidad en el frontend
    date: d.fecha,              // Ya viene corregida del frontend (YYYY-MM-DD)
    startTime: startTime,       // Ya viene en hora de Bogot√° (HH:MM)
    endTime: endTime,          // Calculado correctamente (HH:MM)
    serviceType: d.servicio,   // 'individual' | 'parejas'
    isBooked: true,            // Siempre true para citas confirmadas

    // üë§ Datos del cliente
    nombre: d.nombre,
    email: d.email,
    telefono: d.telefono,
    whatsappJid: d.whatsappJid,
    modalidad: d.modalidad,
    mensaje: d.mensaje || '',
    timezone: d.timezone,

    // üîç Detalles del servicio y modalidad
    servicioDetalle: d.servicioDetalle,
    modalidadDetalle: d.modalidadDetalle,

    // üìã Detalles del evento de calendario
    title: d.title,
    location: d.location,
    description: d.description,

    // üîó Enlaces de Google Calendar
    calendarEventId: googleCalendar.id,
    calendarLink: googleCalendar.htmlLink,

    // ‚è∞ Timestamps de control
    timestamp: d.timestamp,
    fechaCompleta: d.fechaCompleta,
    source: d.source,
    createdAt: new Date().toISOString(),

    // üîß Metadatos adicionales para depuraci√≥n
    processedAt: new Date().toISOString(),
    n8nExecutionId: $execution.id || 'unknown',
    version: '2.0'
  }
}]
